import React from 'react';
import {withRouter} from 'react-router-dom';
import Header from './Header';
import './CreateContact.css'

class CreateContact extends React.Component {
  constructor(props) {
    super(props);
    // this.state = {
    //   username: ''
    // }

    this.createContact = this.createContact.bind(this);
  }


  createConvtact(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('email', this.email.value);

    fetch('http://localhost:5000/create_contact', {
    method: 'POST',
    body: data,
  })
  .then((response) => {
    return response.text();
  })
  .then((data) => {
    let created_contact = data
    console.log(created_contact)
    console.log(typeof created_contact)
    if (created_contact.includes('contact_id')) {
      this.props.history.push('/conversation-list')
    }
    else {
      alert('Echec de la création du contact')
    }
    // window.location.reload(false);
  });
  }



  render() {

      return (
      <> 
      <Header />
        <form onSubmit={this.createContact}>
          <div className="flex">
            <div className="input-style">
              <label for="">Email du contact: </label>
              <input id="email" class="input" type="text" ref={(ref) => { this.email = ref; }} required />
            </div>
          </div>
          <div>
            <button>Créer contact</button>
          </div>
        </form>
      </>
      );
  }
}

export default withRouter(CreateContact);
