import * as React from 'react';
import './ContactsDisplay.css'



export default class ContactsSelect extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          contact: this.props.contacts,
        }
    }

    onTrigger = (event) => {
        let check = document.getElementById(`${this.state.contact.id}`)
        console.log(check)
        if (check.checked == true) {
        this.props.parentCallbackAdd(event.target.value);
        event.preventDefault();
        }

        else {
            this.props.parentCallbackRemove(event.target.value)
        }
    }


    render() {



    return (
        <>
          <div className="members">
            <label>{this.state.contact.username}</label>
            <input type="checkbox" id={this.state.contact.id} value={this.state.contact.email} onChange={this.onTrigger}/>
          </div>
        </>
        )
}
}
